import React, { useState, useEffect } from "react";
import Course from "./course";
import "../style/homet.css";
import Axios from "axios";

export default function Homet() {
  const [list, setList] = useState([]);
  const [listItem, setListItem] = useState("");
  const [name, setName] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/homet").then((response) => {
      const lists = response.data;
      var xl = [];
      lists.map((item) => {
        xl = [...xl, Object.values(item)];
      });
      setList(xl);
    });
  }, []);

  const handleAdd = () => {
    Axios.post("http://localhost:3001/homet", {
      name: name,
    });
  };

  return (
    <div>
      <h2>Teacher's Screen</h2>
      {list.map((item, index) => {
        return (
          <div className="course-item" key={index}>
            <Course name={item[0]} />
            <button>Add quiz</button>
          </div>
        );
      })}
      <input
        type="text"
        value={listItem}
        onChange={(e) => {
          setListItem(e.target.value);
        }}
      />
      <button
        onClick={() => {
          if (listItem === "") {
            alert("Add Courese Name");
            return;
          }
          setList([...list, listItem]);
          setListItem("");
        }}
      >
        Add
      </button>
    </div>
  );
}
